import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import "react-toastify/dist/ReactToastify.css";
import { ToastContainer, toast } from "react-toastify";
import styled from "styled-components";
import Pagination from "./Pagination";

const Container = styled.div`
  padding: 20px;
`;

const Title = styled.h1`
  font-size: 24px;
  margin-bottom: 10px;
`;

const Subtitle = styled.h2`
  font-size: 20px;
  margin-bottom: 10px;
`;

const AddButton = styled(Link)`
  display: inline-block;
  padding: 5px 10px;
  border-radius: 4px;
  background-color: #007bff;
  color: #fff;
  text-decoration: none;
  margin-bottom: 10px;
  margin-right: 10px;
`;

const SearchBar = styled.input`
  padding: 5px;
  border-radius: 4px;
  border: 1px solid #ccc;
  margin-bottom: 10px;
`;

const StyledTable = styled.table`
  width: 100%;
  border-collapse: collapse;
`;

const Th = styled.th`
  background-color: #f5f5f5;
  padding: 10px;
  text-align: left;
`;

const Td = styled.td`
  padding: 10px;
`;

const Button = styled.button`
  padding: 5px 10px;
  border-radius: 4px;
  border: none;
  cursor: pointer;
  margin-right: 5px;

  &.is-success {
    background-color: #5cb85c;
    color: #fff;
  }

  &.is-info {
    background-color: #5bc0de;
    color: #fff;
  }

  &.is-danger {
    background-color: #d9534f;
    color: #fff;
  }
`;

const VitreFumeeList = () => {
  const [vitreFumees, setVitreFumees] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [postsPerPage, setPostsPerPage] = useState(10);
  const [searchTerm, setSearchTerm] = useState("");

  const lastPostIndex = currentPage * postsPerPage;
  const firstPostIndex = lastPostIndex - postsPerPage;
  const currentPosts = vitreFumees
    .filter((vitreFumee) =>
      vitreFumee.mark.toLowerCase().includes(searchTerm.toLowerCase())
    )
    .slice(firstPostIndex, lastPostIndex);

  useEffect(() => {
    getVitreFumees();
  }, []);

  const getVitreFumees = async () => {
    try {
      const response = await axios.get("http://localhost:5000/vitreFumees");
      setVitreFumees(response.data);
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  };

  const deleteVitreFumee = async (vitreFumeeId) => {
    if (window.confirm("Are you sure you want to delete?")) {
      try {
        await axios.delete(
          `http://localhost:5000/vitreFumees/${vitreFumeeId}`
        );
        toast.error("Deleted");
        getVitreFumees();
      } catch (error) {
        console.error("Error deleting item:", error);
      }
    }
  };

  const handleSearch = (event) => {
    setSearchTerm(event.target.value);
    setCurrentPage(1);
  };

  return (
    <Container>
      <ToastContainer
        position="top-center"
        autoClose={5000}
        hideProgressBar={false}
        newestOnTop={false}
        closeOnClick
        rtl={false}
        pauseOnFocusLoss
        draggable
        pauseOnHover
        theme="colored"
      />
      <Title>Vitre Fumees</Title>
      <div>
        <AddButton to="/vitreFumees/add">Add New</AddButton>
        <SearchBar
          type="text"
          placeholder="Search by mark"
          value={searchTerm}
          onChange={handleSearch}
        />
      </div>
      <StyledTable>
        <thead>
          <tr>
            <Th>No</Th>
            <Th>Image</Th>
            <Th>Mark</Th>
            <Th>Fonction</Th>
            <Th>Address</Th>
            <Th>Plaque</Th>
            <Th>Owner name</Th>
            <Th>Created By</Th>
            <Th>Actions</Th>
          </tr>
        </thead>
        <tbody>
          {currentPosts.map((vitreFumee, index) => (
            <tr key={vitreFumee.uuid}>
              <Td>{index + 1}</Td>
              <Td>
                <img
                  height={20}
                  width={20}
                  src={`http://localhost:5000/${vitreFumee.image}`}
                  alt=""
                />
              </Td>
              <Td>{vitreFumee.mark}</Td>
              <Td>{vitreFumee.fonction}</Td>
              <Td>{vitreFumee.address}</Td>
              <Td>{vitreFumee.plaque}</Td>
              <Td>{vitreFumee.owner}</Td>
              <Td>{vitreFumee.user.name}</Td>
              <Td>
                <Link
                  to={`/vitreFumees/view/${vitreFumee.uuid}`}
                  className="button is-small is-success"
                >
                  View
                </Link>
                <Link
                  to={`/vitreFumees/edit/${vitreFumee.uuid}`}
                  className="button is-small is-info"
                >
                  Edit
                </Link>
                <Button
                  onClick={() => deleteVitreFumee(vitreFumee.uuid)}
                  className="button is-small is-danger"
                >
                  Delete
                </Button>
              </Td>
            </tr>
          ))}
        </tbody>
      </StyledTable>
      <Pagination
        totalPosts={vitreFumees.length}
        postsPerPage={postsPerPage}
        setCurrentPage={setCurrentPage}
        currentPage={currentPage}
      />
    </Container>
  );
};

export default VitreFumeeList;
