import React from "react";
import { NavLink, useNavigate } from "react-router-dom";
import { IoPerson, IoHome, IoLogOut, IoCashOutline } from "react-icons/io5";
import { AiOutlineCar } from "react-icons/ai";
import {ImInsertTemplate} from "react-icons/im";
import { useDispatch, useSelector } from "react-redux";
import { LogOut, reset } from "../features/authSlice";

const Sidebar = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const { user } = useSelector((state) => state.auth);

  const logout = () => {
    dispatch(LogOut());
    dispatch(reset());
    navigate("/");
  };

  return (
    <div className="sidebar">
      <aside className="menu pl-2 has-shadow ">
        <p className="menu-label">General</p>
        <ul className="menu-list">
          <li>
            <NavLink to={"/dashboard"}>
              <IoHome 
              //  style={{
              //   color: "",
              //   backgroundColor: "rgba(122,255,0,0.25)",
              //   borderRadius: 20,
              //   fontSize: 24,
              //   padding: 8,
              // }}
              /> Dashboard
            </NavLink>
          </li>
          <li>
            <NavLink to={"/vehicules"}>
              <AiOutlineCar /> Vehicules
            </NavLink>
          </li>
          <li>
            <NavLink to={"/vitreFumees"}>
              <AiOutlineCar /> Vitre fumee
            </NavLink>
          </li>
          <li>
            <NavLink to={"/penaltiess"}>
                  <IoCashOutline /> Penalties 
                </NavLink>
          </li>
        </ul>
        {user && user.role === "admin" && (
          <div>
            {/* <p className="menu-label">Admin</p> */}
            <ul className="menu-list">
              <li>
                <NavLink to={"/users"}>
                  <IoPerson /> Users
                </NavLink>
              </li>
              <li>
                <NavLink to={"/penalties"}>
                  <IoCashOutline /> Penalty Type
                </NavLink>
              </li>
            </ul>
          </div>

        )}

        <p className="menu-label">Settings</p>
        <ul className="menu-list">
          <li>
            <button onClick={logout} className="button is-white">
              <IoLogOut /> Logout
            </button>
          </li>
        </ul>
      </aside>
    </div>
  );
};

export default Sidebar;
