import React, { useState,useEffect } from 'react';
import { Table, Input, Button, Pagination, Modal, Form, InputNumber } from 'antd';
import { SearchOutlined, UserAddOutlined } from '@ant-design/icons';
import axios from "axios";
const { Column } = Table;
const { confirm } = Modal;
const { Item } = Form;

const PoliceStationList = () => {


  const [vitreFumee, setVitreFumee] = useState([]);

  useEffect(() => {
    getVitreFumees();
  }, []);

  const getVitreFumees = async () => {
    const response = await axios.get("http://localhost:5000/vitreFumees");
    setVitreFumee(response.data);
  };
  // const [data, setData] = useState([
  //   { id: 1, name: 'John Doe', age: 25, city: "asd",city: "asd" },
  //   { id: 2, name: 'Jane Smith', age: 32 , city: "asd",city: "asd"},
  //   { id: 3, name: 'Alice Johnson', age: 28 , city: "asd",city: "asd"},
  //   { id: 4, name: 'Bob Anderson', age: 40 , city: "asd",city: "asd"},
  //   { id: 5, name: 'John Doe', age: 25, city: "asd",city: "asd" },
  //   { id: 6, name: 'Jane Smith', age: 32 , city: "asd",city: "asd"},
  //   { id: 7, name: 'Alice Johnson', age: 28 , city: "asd",city: "asd"},
  //   { id: 8, name: 'Bob Anderson', age: 40 , city: "asd",city: "asd"},
  //   { id: 9, name: 'John Doe', age: 25, city: "asd",city: "asd" },
  //   { id: 10, name: 'Jane Smith', age: 32 , city: "asd",city: "asd"},
  //   { id: 11, name: 'Alice Johnson', age: 28 , city: "asd",city: "asd"},
  //   { id: 12, name: 'Bob Anderson', age: 40 , city: "asd",city: "asd"},
  //   // ... more data
  // ]);
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage] = useState(5);
  const [searchTerm, setSearchTerm] = useState('');
  const [isModalVisible, setIsModalVisible] = useState(false);
  const [newUserData, setNewUserData] = useState({ name: '', age: '' });

  // Pagination
  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentItems = vitreFumee.slice(indexOfFirstItem, indexOfLastItem);

  // Handle page change
  const handlePageChange = (pageNumber) => setCurrentPage(pageNumber);

  // Handle search
  const handleSearch = (e) => {
    setSearchTerm(e.target.value);
    setCurrentPage(1);
  };

  // Filter items based on search term
  const filteredItems = currentItems.filter((item) =>
    item.plaque.toLowerCase().includes(searchTerm.toLowerCase())
  );

  // Handle delete
  const handleDelete = (id) => {
    confirm({
      title: 'Confirm Deletion',
      content: 'Are you sure you want to delete this user?',
      onOk() {
        const updatedData = vitreFumee.filter((item) => item.id !== id);
       setVitreFumee(updatedData);
      },
      onCancel() {
        // Do nothing
      },
    });
  };

  // Handle modal visibility
  const showModal = () => {
    setIsModalVisible(true);
  };

  const handleModalOk = () => {
    // Perform form validation
    form
      .validateFields()
      .then((values) => {
        // Generate a new unique ID for the user
        const newUserId = Math.max(...vitreFumee.map((item) => item.id)) + 1;
        // Add the new user to the data array
        const updatedData = [
          ...vitreFumee,
          { id: newUserId, name: values.name, age: values.age },
        ];
        vitreFumee(updatedData);
        // Reset the form and modal visibility
        form.resetFields();
        setIsModalVisible(false);
      })
      .catch((error) => {
        // Validation errors occurred
      });
  };

  const handleModalCancel = () => {
    form.resetFields();
    setIsModalVisible(false);
  };

  // Create form reference
  let form;

  return (
    <div>
      <div style={{ marginBottom: 16 }}>
        <Input
          placeholder="Search by plaque"
          value={searchTerm}
          onChange={handleSearch}
          prefix={<SearchOutlined />}
          style={{ width: 200 }}
        />
        <Button
          type="primary"
          icon={<UserAddOutlined />}
          onClick={showModal}
          style={{ marginLeft: 16 }}
        >
          Add New 
        </Button>
      </div>
      <Table dataSource={filteredItems} pagination={false} style={{ width: 1000 }} >
        <Column title="ID" dataIndex="id" key="id" />
        <Column title="Mark" dataIndex="mark" key="mark" />
        <Column title="Fonction" dataIndex="fonction" key="fonction" />
        <Column title="Owner" dataIndex="owner" key="owner" />
        <Column title="Plaque" dataIndex="plaque" key="plaque" />
        <Column title="address" dataIndex="address" key="address" />
        <Column
          title="Actions"
          key="actions"
          render={(_, record) => (
            <span>
               <Button type="primary" size="small" className="mr-2">
                 View
               </Button>

               <Button type="primary" size="small" className="mr-2" style={{ background: "green" }}>
               Update
              </Button>

               <Button type="primary" danger
               size="small"

               onClick={() => handleDelete(record.id)}
               >
             Delete
            </Button>
             </span>
          )}
        />
      </Table>
      <Pagination
        current={currentPage}
        pageSize={itemsPerPage}
        total={vitreFumee.length}
        onChange={handlePageChange}
        style={{ marginTop: 16 }}


      />
      <Modal
        title="Add New User"
        visible={isModalVisible}
        onOk={handleModalOk}
        onCancel={handleModalCancel}
      >
        <Form
          layout="vertical"
          onFinish={handleModalOk}
          initialValues={newUserData}
          ref={(ref) => (form = ref)}
        >
          <Item
            label="Name"
            name="name"
            rules={[
              { required: true, message: 'Please enter the name' },
              { max: 50, message: 'Name should be maximum 50 characters' },
            ]}
          >
            <Input />
          </Item>
          <Item
            label="Age"
            name="age"
            rules={[
              { required: true, message: 'Please enter the age' },
              { type: 'number', min: 1, max: 150, message: 'Please enter a valid age' },
            ]}
          >
            <InputNumber />
          </Item>
        </Form>
      </Modal>
    </div>
  );
};

export default PoliceStationList;
