import React, { useState,useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import styled from 'styled-components'
const FormAddUser = () => {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confPassword, setConfPassword] = useState("");
  const [role, setRole] = useState("");
  const [agentid, setAgentid] = useState("");
  const [phone, setPhone] = useState("");
  const [image, setImage] = useState("");
  const [msg, setMsg] = useState("");
  const navigate = useNavigate();
  const [policeStations, setPoliceStations] = useState([]);
  const [selectedItem, setSelectedItem] = useState("");

  useEffect(() => {
    getPoliceStations();
  }, []);

  const getPoliceStations = async () => {
    const response = await axios.get("http://localhost:5000/policeStations");
    setPoliceStations(response.data);
  };
  const saveUser = async (e) => {
    e.preventDefault();
    try {
      const formData = new FormData();
      formData.append("name", name);
      formData.append("email", email);
      formData.append("password", password);
      formData.append("confPassword", confPassword);
      formData.append("agentid", agentid);
      formData.append("phone", phone);
      formData.append("role", role);
      formData.append("image", image);
      const config ={
        headers:{
          "Content-Type":"multipart/form-data"
        }
      }

      await axios.post("http://localhost:5000/users", formData,config);
      navigate("/users");
    } catch (error) {
      if (error.response) {
        setMsg(error.response.data.msg);
      }
    }
  };

  const handleSelectChange = (e) =>{
    setSelectedItem(e.target.value)
  }
  return (
    <div>
      <h1 className="title">Users</h1>
      <h2 className="subtitle">Add New User</h2>
      <div className="card is-shadowless">
        <div className="card-content">
          <div className="content">
            <form onSubmit={saveUser}>
              <p className="has-text-centered">{msg}</p>
              <div className="field">
                <label className="label">Name</label>
                <div className="control">
                  <input
                    type="text"
                    className="input"
                    value={name}
                    onChange={(e) => setName(e.target.value)}
                    placeholder="Name"
                  />
                </div>
              </div>
              <div className="field">
                <label className="label">Email</label>
                <div className="control">
                  <input
                    type="text"
                    className="input"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    placeholder="Email"
                  />
                </div>
              </div>
              <div className="field">
                <label className="label">Agent ID</label>
                <div className="control">
                  <input
                    type="text"
                    className="input"
                    value={agentid}
                    onChange={(e) => setAgentid(e.target.value)}
                    placeholder="Agent ID"
                  />
                </div>
              </div>
              <div className="field">
                <label className="label">Phone</label>
                <div className="control">
                  <input
                    type="text"
                    className="input"
                    value={phone}
                    onChange={(e) => setPhone(e.target.value)}
                    placeholder="Phone Number"
                  />
                </div>
              </div>
              {/* <div className="field">
                <label className="label">Password</label>
                <div className="control">
                  <input
                    type="password"
                    className="input"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    placeholder="******"
                  />
                </div>
              </div>
              <div className="field">
                <label className="label">Confirm Password</label>
                <div className="control">
                  <input
                    type="password"
                    className="input"
                    value={confPassword}
                    onChange={(e) => setConfPassword(e.target.value)}
                    placeholder="******"
                  />
                </div>
              </div> */}
              <div className="field">
                <label className="label">Police Station</label>
                <div className="control">
                  <div className="select is-fullwidth">
                  <select value={selectedItem} onChange={handleSelectChange} >
                    {
                    policeStations.map((item)=>(
                    <option key={item.id} value={item.id}>
                      {item.name}
                      </option>
                    ))
                    }
                    </select>
                  </div>
                </div>
              </div>
              <div className="field">
                <label className="label">Role</label>
                <div className="control">
                  <div className="select is-fullwidth">
                    <select
                      value={role}
                      onChange={(e) => setRole(e.target.value)}
                    >
                      <option value="admin">Admin</option>
                      <option value="user">User</option>
                      <option value="police">Police</option>
                    </select>
                  </div>
                </div>
              </div>

              <div className="field">
                <label className="label">Image</label>
                <div className="control">
                  <input
                    type="file"
                    className="input"

                    onChange={(e) => setImage(e.target.files[0])}
                    placeholder="Image"
                  />
                </div>
              </div>

              <div className="field">
                <div className="control">
                  <button type="submit" className="button is-success ">
                    CREATE
                  </button>
                </div>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

export default FormAddUser;
const Button = styled.button`
  background: rgb(37, 150, 190);
  border-radius: 3px;
  //border: 2px solid palevioletred;
 //  color: palevioletred;
  margin: 0 1em;
  padding: 0.25em 1em;
  `
